import UIKit

extension ADSTextField {

    @IBInspectable var localizedString: String {
        set {
            text = newValue.storyboardLocalized
        }
        get {
            return self.localizedString
        }
    }

    @IBInspectable var localizedPlaceHolderString: String {
        set {
            placeholder = newValue.storyboardLocalized
        }
        get {
            return self.localizedPlaceHolderString
        }
    }
}
